mapper.select:
SELECT * FROM play_histories 
WHERE user_id = #{userId} 
ORDER BY play_time DESC

SELECT * FROM play_histories 
WHERE user_id = #{userId} 
ORDER BY play_time DESC 
LIMIT #{offset}, #{pageSize}

SELECT * FROM play_histories 
WHERE user_id = #{userId} AND music_id = #{musicId}
LIMIT 1

mapper.update:
UPDATE play_histories 
SET play_duration = #{playDuration}, play_time = #{playTime}
WHERE id = #{id}
